import React, { useState } from "react";
import "./HotelOwnerDashboard.css";

const HotelOwnerDashboard = () => {
  const [locationInput, setLocationInput] = useState("");
  const [embedUrl, setEmbedUrl] = useState("");

  const generateEmbedUrl = (location) => {
    if (location.includes("google.com/maps")) {
      // If they pasted a link like https://www.google.com/maps/place/...
      const query = encodeURIComponent(location);
      return `https://www.google.com/maps/embed/v1/place?key=YOUR_GOOGLE_MAPS_EMBED_API_KEY&q=${query}`;
    } else {
      // If they just typed an address
      const query = encodeURIComponent(location);
      return `https://www.google.com/maps/embed/v1/place?key=YOUR_GOOGLE_MAPS_EMBED_API_KEY&q=${query}`;
    }
  };

  const handleGenerateEmbed = () => {
    const embed = generateEmbedUrl(locationInput);
    setEmbedUrl(embed);
  };

  return (
    <div className="owner-dashboard">
      <h1>Hotel Owner Dashboard</h1>

      <label>Enter Location / Google Maps Link:</label>
      <input
        type="text"
        placeholder="Ex: Silver Crown Hotel, Anuradhapura"
        value={locationInput}
        onChange={(e) => setLocationInput(e.target.value)}
      />

      <button onClick={handleGenerateEmbed}>Generate Map</button>

      {embedUrl && (
        <div className="map-preview">
          <h2>Map Preview</h2>
          <iframe
            src={embedUrl}
            width="600"
            height="450"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
          ></iframe>
        </div>
      )}
    </div>
  );
};

export default HotelOwnerDashboard;
