import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
// Remove unused import. UserDashboard is rendered via route after login.
import "./login.css";

const LoginPage = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:8000/api/login.php", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          username: usernameOrEmail, // ✅ keep "username" to match PHP
          password: password,
        }),
      });

      const data = await res.json();
      alert(data.message);

      if (data.message === "Login successful") {
        localStorage.setItem("user", JSON.stringify(data.user));
        if (data.user.dashboard === "tuorist") {
          navigate("/tuorist-dashboard", {
            state: { name: data.user.name, role: data.user.role },
          });
        } else {
          // navigate to other dashboard or home
          navigate("/");
        }
      }
    } catch (err) {
      console.error(err);
      alert("Something went wrong. Try again.");
    }
  };

  return (
    <div className="login-page">
      <div className="login-background"></div>
      <div className="login-container">
        <h2>Welcome Back</h2>
        <p className="subtitle">Login to continue your journey</p>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="usernameOrEmail">Username or Email</label>
            <input
              type="text"
              id="usernameOrEmail"
              name="usernameOrEmail"
              placeholder="Username or Email"
              value={usernameOrEmail}
              onChange={(e) => setUsernameOrEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
          <div className="login-links">
            <a href="#">Forgot Password?</a>
            <span> | </span>
            <Link to="/signup">Create Account</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
