import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
// Remove unused import. UserDashboard is rendered via route after login.
import "./login.css";

const LoginPage = () => {
  const [usernameOrEmail, setUsernameOrEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showError, setShowError] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setShowError(false);
    setErrorMessage("");

    fetch("http://localhost:8000/api/login.php", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        username: usernameOrEmail,
        password: password,
      }),
    })
      .then(async (res) => {
        const data = await res.json();
        if (!res.ok) {
          throw new Error(data.message || "Login failed");
        }
        return data;
      })
      .then((result) => {
        console.log('User role:', result.user.role); // Debug log
        if (result.message === "Login successful") {
          localStorage.setItem("userRole", result.user.role);
          localStorage.setItem("userName", result.user.name);
          localStorage.setItem("userEmail", result.user.email);
          if (result.user.role && result.user.role.toLowerCase() === "tuorist") {
            navigate("/tuorist-dashboard", { state: { name: result.user.name, role: result.user.role } });
          } else {
            navigate("/");
          }
        } else {
          setErrorMessage(result.message || "Login failed");
          setShowError(true);
          setTimeout(() => setShowError(false), 3000);
        }
      })
      .catch((err) => {
        setErrorMessage("Error: " + err.message);
        setShowError(true);
        setTimeout(() => setShowError(false), 3000);
      });
  };

  return (
    <div className="login-page">
      <div className="login-background"></div>
      <div className="login-container">
        <h2>Welcome Back</h2>
        <p className="subtitle">Login to continue your journey</p>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label htmlFor="usernameOrEmail">Username or Email</label>
            <input
              type="text"
              id="usernameOrEmail"
              name="usernameOrEmail"
              placeholder="Username or Email"
              value={usernameOrEmail}
              onChange={(e) => setUsernameOrEmail(e.target.value)}
              required
            />
          </div>
          <div className="input-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="••••••••"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
          {showError && <div className="error-message">{errorMessage}</div>}
          <div className="login-links">
            <a href="#">Forgot Password?</a>
            <span> | </span>
            <Link to="/signup">Create Account</Link>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
