import React, { useState } from "react";
import "./admin.css";

const HotelDashboard = () => {
  const [selectedPackage, setSelectedPackage] = useState(null);

  const hotelPackages = [
    { id: 1, name: "Luxury Package", price: 1500 },
    { id: 2, name: "Family Package", price: 800 },
    { id: 3, name: "Budget Package", price: 400 },
  ];

  const freeFacilities = ["Swimming Pool", "Gym", "Free Wi-Fi", "Parking"];

  const handleSelectPackage = (pkg) => {
    setSelectedPackage(pkg);
  };

  const handleGenerateBill = () => {
    if (!selectedPackage) {
      alert("Please select a package first!");
    } else {
      alert(
        `Bill generated for: ${selectedPackage.name}\nTotal Cost: $${selectedPackage.price}`
      );
    }
  };

  return (
    <div className="hotel-dashboard">
      <header>
        <h1>Hotel Dashboard</h1>
        <p>Welcome! Manage your hotel offers and bookings.</p>
      </header>

      <div className="dashboard-content">
        <section className="section">
          <h2>Hotel Packages</h2>
          <ul className="package-list">
            {hotelPackages.map((pkg) => (
              <li key={pkg.id}>
                <span>{pkg.name} - ${pkg.price}</span>
                <button onClick={() => handleSelectPackage(pkg)}>
                  {selectedPackage?.id === pkg.id ? "Selected" : "Select"}
                </button>
              </li>
            ))}
          </ul>
        </section>

        <section className="section">
          <h2>Hotel Locations</h2>
          <iframe
            title="Hotel Location Map"
            src="https://maps.google.com/maps?q=Colombo%20Sri%20Lanka&t=&z=13&ie=UTF8&iwloc=&output=embed"
            width="100%"
            height="250"
            style={{ border: 0 }}
            allowFullScreen=""
            loading="lazy"
          ></iframe>
        </section>

        <section className="section">
          <h2>Free Facilities</h2>
          <ul className="facility-list">
            {freeFacilities.map((facility, index) => (
              <li key={index}>{facility}</li>
            ))}
          </ul>
        </section>

        <section className="section">
          <h2>Generate Bill</h2>
          <button onClick={handleGenerateBill} className="generate-bill-btn">
            Generate Bill
          </button>
          {selectedPackage && (
            <div className="bill-summary">
              <p>
                <strong>Package:</strong> {selectedPackage.name}
              </p>
              <p>
                <strong>Total:</strong> ${selectedPackage.price}
              </p>
            </div>
          )}
        </section>
      </div>
    </div>
  );
};

export default HotelDashboard;
